INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000032','o_aom','[수집]AOM','AOM 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, AOM 시스템은 단말에 설치된 AOM(Always On Management) Client에서 생성한 Cell 단위 무선 품질에 주기적인 수집과 관리를 지원하는 시스템 ','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000033','o_apollo','[수집]APOLLO','APOLLO 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, APOLLO는 ACCESS망에서 발생하는 다양한 데이터에 대한 데이터 분석 시스템임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000034','o_cem','[수집]TPANI CEM','T-PANI 내의 CEM(Customer Experience Management) 데이터을 수집, 원본 그대로 저장하는 영역으로, CEM(Customer Experience Management)는 고객 체감 품질을 CEI라는 지수로 관리하여, 고객 대응, 지역 별 품질 개선에 활용함','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000035','o_cims','[수집]CIMS','CIMS 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, CIMS는 각 장비에서 처리되는 Command 이력 정보 관리하는 시스템임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000036','o_ciss','[수집]CISS','CISS 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, CISS는 MOIRA에 포함된 모듈로, Access망의 기지국, 광중계기, RF중계기, WiFi 등 시설을 Map 기반 관리가 가능하고 투자필요지역의 VOC, 품질을 관리하여 효율적으로 투자가 이루어질 수 있도록 지원하는 서비스임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000037','o_cms','[수집]CMS','CMS 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, CMS(Configuration Management System)는 기지국/ICT 등 시설 정보, Sector 구성 등 Configuration 정보, VLAN 구성 정보 등의 데이터를 가지고 있으며, 각종 기반 장비 내에 입력되어 있는 설정 정보를 통합 관리하는 시스템임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000038','o_common_raw','[수집]TPANI Probe Data','공통으로 활용 최소 단위의 원본 데이터를 수집 보관 영역으로, 시그널 데이터와 유저 데이터로 구분하여, CEI 및 기타 T-PANI 데이터 통계의 rawdata로 사용되는 T-Probe에서 생성하는 xDR 데이터 관리 영역','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000039','o_datainfo','[수집]DataInfo','DATAINFO 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역이며, DATAINFO는 네트워크 장비, 서버 장비의 사용 및 설정 데이터,  설치 소프트웨어 정보를 수집, 관리하는 시스템으로, 수집 대상은 네트워크 망 운영과 관련된 물리 서버 머신, 가상 머신, 스토리지, 보안장비, 소프트웨어 설치 정보, 스위치 장비 등이 대상이 됨','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000040','o_dbm','[수집]DBM','DBM 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, DBM(DataBase Marketing) 시스템은 SKT 마케팅 데이터와 관련된 DW(Data Warehouse) 시스템으로, SWING을 원천으로 하여, 마케팅 필요 정보에 대한 분석 업무를 지원하는 시스템임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000041','o_epot','[수집]MOIRA ePOT','MOIRA의 EPOT 모듈에서 수집된 데이터를 있는 그대로 저장하는 영역으로, EPOT은 트래픽 데이터 관리와 트래픽 엔지니어링(주파수 엔지니어링, 채널카드 엔지니어링)을 위한 내부 분석을 지원하며, 이를 위해 장비별 트래픽 부하율 및 소요 물량 산출 기능을 가지고 있는 서비스임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000042','o_erp','[수집]ERP','ERP 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, ERP는 전사 주요 자원에 대한 마스터 데이터를 중심으로 재무, 회계, HR, 물류, 투자 업무 처리 시스템이며, ERP에서 수집되는 주요 데이터는 건물, 시설, 장비 등의 자산과 관련된 마스터 및 주소 마스터 등의 데이터임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000043','o_fims','[수집]FIMS','FIMS 시스템에서 수집된 데이터를 있는 그대로 저장하는 영영이며, FIMS는 Access망 투자 사업의 구축 관리 시스템으로, ERP로 부터 엔지니어링 정보를 연동받아 공정, 정산, 자재관리, 인수인계 등의 절차를 수행할 수 있으며, 이를 위해 필요한 표준설계, 사업관리, Community 등의 기능과 구축 관련 분야 통계 분석을 위한 자료 제공 및 관리 지표의 Report 기능을 제공함','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000044','o_hdv','[수집]HD Voice','HD Voice에 발생한 데이터를 있는 그대로 수집하여 저장하는 영역으로, LTE Packet 기반 고품질 음성 통화 서비스인 HD Voice 서비스의 Signal 및 음성 품질에 대한 정보 생성 및 통계 정보 데이터로 구성되며, 이 데이터는 T-PANI CEI 중 HDV CEI 의 RAW DATA로 활용되는 데이터이기도 함','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000045','o_iam','[수집]IAM','IAM 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, IAM 시스템 데이터 중, EagleEye 시스템에 의해 수집되는 품질 불량 지역에 대한 무선구간 품질 정보 관리함','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000046','o_ifms','[수집]IFMS','IFMS 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, IFMS는 3G / LTE의 기지국 및 ICT의 주 장비 시스템에서 발생하는 HW/SW 등 alarm, Fault에 대한 데이터를 관리함','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000047','o_ifss','[수집]IFSS','IFSS 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, IFSS에서 관리하는 서비스 중단 인덱스 정보가 주요 수집 대상 데이터임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000048','o_imap','[수집]IMAP','IMAP 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, IMAP 시스템은 지도를 기반으로 한 통합망(2G/1X/W/LTE) 품질 감시 시스템이며, 행정구역단위 통합망 품질 감시 및 서비스 장애 지역 표시 및 피해 가입자수 파악 등에 활용됨','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000049','o_iqa','[수집]IQA','IQA 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역이며, IQA는 망 품질 관련 데이터를 통합하여 분석하기 위한 시스템으로, 본사/지역 측정결과 단순가공 및 통계 자동화, 타시스템 연계분석 기능 및 품질/VOC 불량지역 개선이력 관리, 측정툴 연계 등의 기능을 가지고 있음','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000050','o_ircs','[수집]IRCS','IRCS 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, IRCS는 중계기 구성정보, 상태정보, 알람 및 통계 정보, 작업 관리 기능을 가짐','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000051','o_irms','[수집]IRMS','IRMS 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, IRMS는 정류기, 냉/난방기, 소화장비 및 밧데리 부대장비의 제원/시설수 및 정류기의 성능 정보 관리 기능을 가짐','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000052','o_iwcs','[수집]IWCS','IWCS 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역이며, IWCS는 통합 작업 관제 시스템임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000053','o_lfas','[수집]LFAS','LFAS 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역이며, LFAS는 LTE Access 시스템에서 발생하는 F3000 (Call Fail 데이터)를 수집하여 시스템으로, F3000 기준으로 통계 정보를 관리하는 기능을 가짐','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000054','o_mfas','[수집]MFAS','MFAS 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, MFAS는 wcdma에서 발생하는 f3000 메시지(단말의 call fail 발생시 시스템에서 발생하는 메시지)를 수집하여 장비 및 Fail Reason별 통계 정보를 관리하는 기능을 가짐','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000055','o_moira','[수집]MORIA','MOIRA 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, MOIRA는 ACCESS 망에 대한 투자계획 집행 분석 모듈인 CISS(Coverage Investment Supporting System), 트래픽 분석 모듈인 ePOT(ePOT, Engineering Performance Optimization Tool), 비용 계획 수립 기능을 가진 BPM(Business Plan Modeling)의 3개 모듈로 구성된,  네트워크 인프라 영역의 투자와 집행 내역에 대한 최적화와 이를 위한 분석 시스템임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000056','o_nams','[수집]NAMS','NAMS 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역이며, NAMS는 네트워크 시설물 및 자신의 운용 관리 시스템임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000057','o_neos','[수집]NEOS','NEOS 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, NEOS는 자회사 "네트웍오엔에스"의 현장 업무 결과를 작성하여 기록하고, 이를 기반으로 업무 분석 및 직무 분석에 활용하는 시스템임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000058','o_ngenius','[수집]NGENIUS','NGENIUS 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역이며, NGENIUS는 IP Packet 분석툴로서, IP망 백본에 설치되어 주고 받는 Packet 분석을 지원하고, Network Node간의 Packet Probe 및 Analyzer 기능을 가지며, 고객 체감 및 시스템 이슈 시 패킷 기반의 분석 및 통계 제공을 통해 신속한 RCA가 가능하도록 지원함','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000059','o_nis','[수집]NIS','NIS 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, NIS는 통화품질 상담, VOC 이관 및 처리를 위하여 네트워크 시설 및 담당자 정보 제공, VOC 접수에서 처리 업무 수행 환경을 제공하며, 상담 NIS, 일반 NIS, 통계 NIS, 현장 방문 시간 관리, 모바일 NIS로 구성됨','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000060','o_nwacs','[수집]NWACS','NWACS(Network Warning & Control System)에서 수집된 데이터를 있는 그대로 저장하는 영역으로, NWACS는 Ok-net 통합상황판 내의 NWACS > 용량관리 메뉴에서 사용되는 데이터를 관리하는 하나의 모듈을 의미함','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000061','o_oknet','[수집]OK NET','TANGO 이전의 Infra 통합 접근 포털인 OK-Net 내에 존재하는 각종 통계 데이터를 수집 관리하는 영역으로, OK-Net 통합상황판 내의 집계 데이터로 구성됨','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000062','o_oss','[수집]OSS','OSS 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, WCDMA와 LTE 기지국에 대한 Barring 적용 시나리오 및 Barring 적용 국소에 대한 데이터를 가지고 있음','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000063','o_pss','[수집]PSS','PSS 시스템에서 수집된 데이터를 있는 그대로 저장하는 역으로, PSS는 기지국 및 교환기 등의 시스템에서 발생한 데이터를 수집하여 주요 KPI 지표에 대한 통계 정보를 생성 관리하는 시스템임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000064','o_samson_adam','[수집]TPANI 트래픽 통계','T-PANI 시스템의 데이터를 모듈별로 분할하여 있는 그대로 저장하는 영역 중 하나이며, 앱/장비/단말/요금제/연령대 등의 기준으로 생성되는 트래픽 데이터를 최소 1시간 주기의 통계로 생성한 트래픽 통계 정보 관리 영역','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000065','o_samson_cem','[수집]TPANI CEM','T-PANI 시스템의 데이터를 모듈별로 분할하여 있는 그대로 저장하는 영역 중 하나이며, 고객 경험에 대한 CEI 데이터를 개인/장비/지역/단말/담당조직 등의 기준으로 생성하고, 이와 관련된 정보를 제공하는 영역 (CEI 는 크게 종합, HDV, CSFB, DATA 관점의 CEI 를 100점 만점 기준으로 산출하며, 각 유형별 CEI 도 지표별로 세분화되어 정보를 제공)','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000066','o_samson_cem_ber','[수집]TPANI CEM 부정경험','T-PANI 시스템의 데이터를 모듈별로 분할하여 있는 그대로 저장하는 영역 중의 하나이며, 고객 경험에 대한 CEI 데이터를 기준으로 부정 경험(Bad CE,Bad Customer Experience)을 한 고객이 얼마나 많이 분포되어 있는지를 확인할 수 있는 비율(BER, Bad Experience Ratio) 정보를 생성, 제공하는 영역','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000067','o_samson_cem_kpi','[수집]TPANI CEM KPI','T-PANI 시스템의 데이터를 모듈별로 분할하여 있는 그대로 수집 저장 관리하는 영역 중의 하나이며, 고객 경험에 대한 CEI 데이터를 기준으로 본부별로 KPI 목표를 설정하여 관리하고, 관련된 정보를 제공하는 영역','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000068','o_samson_cm','[수집]TPANI 공통','T-PANI 시스템의 데이터를 모듈별로 분할하여 있는 그대로 저장하는 영역 중 하나이며, T-PANI 시스템에서 활용하는 기지국 이름, 담당 조직 등의 시설에 대한 구성정보 데이터 영역(해당 데이터의 원천은 CMS 에서 제공을 받음)','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000069','o_samson_dw','[수집]TPANI Analysis','T-PANI 시스템의 데이터를 모듈별로 분할하여 저장하는 영역 중 하나이며, Probe 기반 LTE 데이터를 이용한 고객 경험 분석 및 LTE Network/Application Service 트래픽 분석 데이터가 저장됨','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000070','o_samson_event','[수집]TPANI EVENT','T-PANI 시스템의 데이터를 모듈별로 분할하여 저장하는 영역 중 하나이며, T-PANI 시스템 데이터를 활용하여 주요한 사회적 이벤트(불꽃 축제, 월드컵 등) 시점의 데이터를 분석한 정보를 제공하는 영역','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000071','o_samson_pm','[수집]TPANI PM','T-PANI 시스템의 데이터를 모듈별로 분할하여 저장하는 영역 중 하나이며, T-PANI 시스템의 N/W Topology 에 해당하는 데이터 통계로 최소 1분 단위의 장비 및 구간별 성능 통계 데이터를 제공하는 영역 (TPANI_NW와 유사한 정보를 제공하지만, SAMSON_PM에서는 T-PANI 의 실시간 처리 클러스터(Storm) 에서 처리한 연산결과를 그대로 저장하는 것으로, CELL 단위 상세 정보는 TPANI_NW 데이터베이스에서 조회 가능)','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000072','o_samson_sgsn','[수집]TPANI SGSN','T-PANI 시스템의 데이터를 모듈별로 분할하여 저장하는 영역 중 하나이며, T-PANI 시스템에서 3G 천이의 기준이 되는 SGSN(Service GPRS Support Node) Mobility 카운트 정보를 제공','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000073','o_sdn','[수집]SDN','SDN 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, SDN은 IP망 시스템에 대한 장비 제원 기반 IP 장비 구성, 성능, 장애 정보의 관리하는 시스템임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000075','o_smileq','[수집]SMILE Q','SMILEQ 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, 고객의 VoC 수집 현황을 활용한 시스템/지역별 VoC 통계 정보 관리','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000076','o_son','[수집]SON','A-SON(SKT SON, SKT Self Organized Network) 시스템에서 수집된 데이터를 있는 그대로 저장하는 영역으로, A-SON은 NW기술원을 중심으로 기존에 구축된 LTE 무선망 기지국 운용 자동화 체계임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000077','o_tango_a','[수집]TANGO Analysis','TANGO내의 TANGO-A(Analysis) 모듈에서 수집된 데이터를 있는 그대로 저장하는 영역으로, TANGO-A 모듈은 TANGO내의 실시간 데이터 수집 및 분석을 지원하는 서비스임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000078','o_tango_ec','[수집]TANGO Eng/Const.','TANGO내의 TANGO EC(Engineering & Contruction) 모듈에서 수집된 데이터를 있는 그대로 저장하는 영역으로, TANGO-EC는 네트워크 Engineering(설계)과 Contruction(구축) 업무를 지원하는 모듈임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000079','o_tango_g','[수집]TANGO 공통','TANGO내의 TANGO-G 모듈의 데이터를 있는 그대로 수집하여 저장하는 영역으로, TANGO-G의 데이터는 차세대 OSS 플랫폼인 TANGO의 기반이 되는 각종 공통 정보 및 데이터를 관리하는 모듈임','DAT_L0','DBP_01','10000');
INSERT INTO mp_md_database (id,physical_nm,logical_nm,description,layer_id,purpose_id,instance_id) VALUES ('1000080','o_tango_i','[수집]TANGO 시설/장비','TANGO내의 TANGO-I(Inventory) 모듈에ㅐ서  수집된 데이터를 있는 그대로 저장하는 영역으로, TANGO-I는 네트워크 서비스 운용에서 핵심이되는 시설, 건물, 장비에 대한 마스터 정보를 통합 관리하는 모듈임','DAT_L0','DBP_01','10000');
