INSERT INTO mp_md_instance (id,physical_nm,logical_nm,description,connection_info,connection_protocol,product_nm,product_version,provider_nm) VALUES ('10000','IDW_HDP','Infra DW 메인 DB',' Infra 센터 비즈니스 활동의 최적화를 위한 데이터 분석 및 활용을 지원하는 통합 데이터베이스로, TANGO 및 Infra 센터 내의 다양한 시스템에서 발생하는 데이터를 중심으로 관련된 데이터를 수집하여 통합 관리하며, 개인정보 성격의 데이터는 모두 비식별화하여 저장함
INSERT INTO mp_md_instance (id,physical_nm,logical_nm,description,connection_info,connection_protocol,product_nm,product_version,provider_nm) VALUES ('10001','IDW_DRD','Infra 메타트론 워크북 DB','Infra 센터 내의 일반적인 구성원 전체가 간단한 데이터 분석 및 시각화 정보 생성을 직접할 수 있는 도구인 Metatron의 워크북(Workbook)에 연결된 데이터베이스로, Dimension, Measure로 구분하여 모든 테이블의 속성을 정의하는 구조로 구성되어 있음',null,null,'Druid','Driud V2.0','Open Source');
