insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('595','GGSN','Gateway GPRS Support Node','GGSN','GPRS(General Packet Radio Service) 기간망과 외부 패킷 데이터 망간의 접속 기능을 담당하는 노드. 패킷 교환 지원 노드(SGSN)로부터 오는 GPRS 패킷을 적당한 패킷 데이터 프로토콜(PDP) 형식(예: IP, X.25)으로 변환하여 전송하고 착신 패킷 데이터의 PDP 주소를 수신자의 GSM 주소로 변환하는 기능을 가지고 있다. 또한 SGSN의 위치 레지스터에 있는 현 사용자의 SGSN의 주소와 사용자 프로파일을 저장하고 인증과 요금 부과 기능도 수행한다.','N','N','Y','Y','N',null);
