insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('600','GIS조수','GIS Cable Count','GCCNT','GIS 케이블의 수','N','N','Y','Y','N',null);
insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('601','GMLC','Gateway mobile Location Center','GMLC','(관문 이동 위치 센터) 교환망과 위치 정보 서버 간의 게이트웨이 역할을 담당하는 코어망 장비. 위성 위치 확인 시스템(GPS)이나 기지국에 의한 다양한 방식을 통해 확인한 가입자의 위치 정보를 필요로 하는 시스템에 전달해 주는 시스템이다. 예를 들어, 인터넷, 각종 지능망 서비스 등에서 단말기의 위치 정보가 필요하면 위치 정보 서버에서 필요한 데이터를 찾아 전달하는 것으로, 이동 위치 정보 센터(MPC: Moblie Positioning Center)와 동일한 역할을 수행한다.','N','N','Y','Y','N',null);
insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('602','GMT','Greenwich Mean Time','GMT','그리니치 표준시 ','N','N','Y','Y','N',null);
insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('603','GN','GN','GN','GPRS망 내 동일 PLMN 상의 GSN 간 인터페이스.','N','N','N','Y','N',null);
insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('604','GNB','Global Navigation Bar','GNB','웹 페이지 최 상단의 메뉴 구성을 의미하며, 하위 메뉴를 LNB(Local Navigation Bar)라 부름','N','N','Y','Y','N',null);
insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('605','GP','GP','GP','GPRS망 내 서로 다른 PLMN 내의 GSN 간 인터페이스이다.','N','N','N','Y','N',null);
insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('606','GPRS','General Packet Radio Service','GPRS','(일반 패킷 무선 서비스) 스웨덴 에릭슨사가 중심이 되어 개발한 세계 최초의 무선 데이터 통신 서비스. 유럽 전기 통신 표준 협회(ETSI)가 표준화한 유럽 디지털 이동 전화 방식 GSM(global system for communication)망을 기초로 한 패킷 통신 서비스를 말한다. 동시에 8통화가 이루어졌을 때 이론적인 최대 속도는 171.2kbps 이지만 GPRS 이용자가 실제로 이용할 수 있는 속도는 28.8kbps로 현재의 고정 전화망의 속도와 같으며, GSM망에서 패킷 통신을 위한 기술이다. 이 서비스는 이동 통신 환경에서 웹 브라우징 등 각종 인터넷 프로토콜(IP) 기반 서비스와 데이터 통신 서비스를 효과적으로 구현해 준다.','N','N','Y','Y','N',null);
insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('607','GPS','Global Positioning System','GPS','미국 국방부(DOD)가 개발하여 추진한 전 지구적 무선 항행 위성 시스템. 지구 어디에서나 항상 4개 이상의 위성이 시계(視界) 내에 있도록 배치되기 때문에, 이용자는 이들 위성 중에서 적당한 4개를 선택하여 그것들로부터의 시각(時刻) 신호를 수신하여 각각의 거리를 측정한다. 4개 위성의 위치는 알려져 있으므로, 이 측정에서 이용자의 위도/경도/고도의 3차원의 위치와 시계(時計)의 시각 편차를 알 수 있다.','N','N','Y','Y','N',null);
insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('608','GRB','GRB','GRB','GRB','N','N','N','Y','N',null);
insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('609','GRE','Generic Routing Encapsulation','GRE','(일반 라우팅 캡슐화) 네트워크를 통한 보안 터널을 형성하기 위해 IP 위에 암호 루틴을 삽입해 만들어지는 암호화 과정. 점 대 점 터널링 통신 규약(PPTP) 등의 원격 접근 서버(RAS) 서비스의 선결 조건이 된다.','N','N','Y','Y','N',null);
insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('610','GROUD','GROUD','GROUD','GROUD','N','N','N','Y','N',null);
insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('611','GSDK','GSDK','GSDK','주소관리센터','N','N','N','Y','N',null);
insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('612','GSN','Gigabyte System Network','GSN','고성능 병렬 인터페이스(HIPPI), 이더넷, 기타 표준망과 호환을 유지하면서 고성능 네트워크 기능을 제공하는 미국표준협회(ANSI) 표준 네트워킹 기술. 800MBps 대역폭과 낮은 지연 시간이 특징이며, 다중 기가비트 네트워크에서 트래픽 집합용 백본으로서 적합하다. 6400Mbps 전이중 무장애 데이터 전송이 가능하며, 네트워크 정체를 경감하기 위해 데이터 흐름 제어 링크를 제공한다.','N','N','Y','Y','N',null);
insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('613','GSR','','GSR','ISP의 백본용 라우터로써 사용되는 대용량 기종을 말함','N','N','N','Y','N',null);
insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('614','GTP','GPRS Tunneling protocol','GTP','GPRS망 내 SGSN과 GSN 간 패킷 데이터와 시그널링 정보 흐름을 처리한다. GTP는 GPRS망의 Gn과 Gp 인터페이스에 정의되어 있다','N','N','Y','Y','N',null);
