insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('624','HA','Home Agent','HA','현재 이동 노드를 관리하고 있는 호스트 또는 라우터. 이동노드의 원위치(홈주소)를 유지관리하고, 이동한 위치(CoA)도 동시에 저장.','N','N','Y','Y','N',null);
insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('625','HARQ','Hybrid Automatic Repeat Request','HARQ','송신단에서 Data를 FEC로 인코딩하고, 패리티 비트를 바로 덧붙여서 보내거나 수신단에서 요청 시 패리티 비트를 전송한다. Reed-Solomon code와 같이 오류 검출을 하며 FEC를 하는 경우 오류 검출 Code는 생략할 수 있다. 결론적으로, Hybrid ARQ는 전송 선로가 좋지 않은 경우에 ARQ보다 전송 효율이 높아지나, 선로 환경이 좋은 곳에서는 Redundancy가 부가되므로 전송 효율을 낮게 한다.','N','N','Y','Y','N',null);
insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('626','HDVOICE','Hdvoice','HDV','통화음질이 HD수준으로 향상되는 기술입니다. 음성통화도 3G → LTE로 데이터를 처리해 기존보다 음질이 향상되었습니다. VoLTE = HD통화 = HD보이스 모두 같은 의미로 사용되며, 블루투스 제품에는 HD보이스라는 용어로 쓰이고 있습니다. 단, 스마트폰과 통신사, 블루투스가 VoLTE를 지원해야 사용할 수 있으며, 같은 통신사끼리의 통화에만 적용됩니다.','N','N','N','Y','N',null);
