insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('633','HLR','Home Location Register','HLR','이동 통신 교환기에서 가입자의 단말기 정보, 가입 정보, 위치 정보와 인증 기능 따위를 지닌 이동 가입자 데이터베이스. 이동 전화 교환국(MSC)과 방문자 위치 레지스터(VLR) 노드와 함께 신호망 프로토콜(SS7)을 제어하고, 또한 입중계 호(incoming call)를 완료하고 가입자 정보를 갱신하기 위해 VLR과 함께 트랜잭션을 수행한다.','N','N','Y','Y','N',null);
