insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('639','HSS','Home Subscriber Server','HSS','가입자 정보를 관리하는 중앙 집중화된 데이터베이스. 이동 통신의 홈 위치 레지스터(HLR)에서 발전한 것으로 사용자 등록/변경 관리, 인증, 권한 부여, 로케이션, 세션 라우팅, 과금 등의 호/세션 제어를 위한 모든 가입자 정보를 관리한다.','N','N','Y','Y','N',null);
insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('640','HSUPA','High Speed Uplink Packet Access','HSUPA','3세대 이동통신 규격 중인 하나인 HSDPA(고속하향패킷접속)에서 업로드 속도를 증가시킨 규격이다. 이는 흔히 3.5세대(G) 이동통신 서비스로 불리는 WCDMA/HSDPA보다 한 단계 진일보한 차세대 기술이다.','N','N','Y','Y','N',null);
