insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('663','IMA','Inverse Multiplexing over ATM','IMA','ATM망에서 2이상의 회선을 통하여 ATM 셀을 전송하는 방법을 규정한 ATM 포럼 표준. T1~T3 회선이 사용되며, 높은 대역폭을 형성하고 다수의 가상 채널이 집합될 수 있도록 하나의 논리적 링크를 만들기 위해 다수의 회선을 조합한다.','N','N','Y','Y','N',null);
