insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('665','IMEI','International Mobile Equipment Identity','IMEI','(국제 이동 단말기 식별 번호) 전 지구적 이동 통신 시스템(GSM) 이동 단말기가 서로를 고유하게 식별할 수 있도록 이동 단말기에 할당된 식별 번호. 이 번호는 형식 승인 코드, 최종 조합 코드 및 일련 번호를 포함하여 15자리로 구성된다.','N','N','Y','Y','N',null);
