insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('679','IPC','InterProcess Communication','IPC','프로세스 간의 통신 기법. 크게 공유 기억 장치 기법과 메시지 시스템 기법으로 나누어진다. 공유 기억 장치 기법은 통신하는 프로세스 간에 어떤 변수를 공유하여 이 공유 변수를 이용하여 정보를 교환하도록 한다. 메시지 시스템 방법은 통신을 제공하는 책임을 운영 체계가 가지고 프로세스가 메시지를 교환할 수 있도록 한다.','N','N','Y','Y','N',null);
