insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('683','IPSEC','Internet Protocol Security','IPSEC','네트워크 계층인 인터넷 프로토콜에서 보안성을 제공해 주는 표준화된 기술로 데이터 송신자의 인증을 허용하는 인증 헤더(AH)와, 송신자의 인증 및 데이터 암호화를 함께 지원하는 보안 페이로드 캡슐화(ESP: Encapsulating Security Payload), 보안 연관(SA: security association) 등이 있으며, 보안 게이트웨이 간의 보안 터널을 제공하는 터널모드와 종단 호스트 간의 보안 터널을 제공하는 트랜스포드 모드 등 2가지 모드를 제공한다. IPv4에서는 선택으로 IPv6에서는 필수로 제공하도록 되어 있다.','N','N','Y','Y','N',null);
