insert into mp_md_dictionary(id, nm_kr, nm_en, abbr, description, classifi_yn, homonym_yn, compound_yn, use_col_yn, use_biz_yn, category_id) values ('909','PDE','PDE','PDE','PDE','N','N','N','Y','N',null);
